INSERT INTO `bardar_site_plugins` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `plugincode`, `locked`, `properties`, `disabled`, `moduleguid`, `static`, `static_file`) VALUES
(1, 0, 0, 'TinyMCE Rich Text Editor', 'TinyMCE Rich Text Editor runtime hooks - registers and includes javascripts on document edit pages', 0, 2, 0, '/**\n * TinyMCE Rich Tech Editor Plugin\n *\n * @package tinymcerte\n * @subpackage plugin\n *\n * @var modX $modx\n * @var array $scriptProperties\n */\n\n$className = \'TinyMCERTE\\Plugins\\Events\\\\\' . $modx->event->name;\n\n$corePath = $modx->getOption(\'tinymcerte.core_path\', null, $modx->getOption(\'core_path\') . \'components/tinymcerte/\');\n/** @var TinyMCERTE $tinymcerte */\n$tinymcerte = $modx->getService(\'tinymcerte\', \'TinyMCERTE\', $corePath . \'model/tinymcerte/\', [\n    \'core_path\' => $corePath\n]);\n\nif ($tinymcerte) {\n    if (class_exists($className)) {\n        $handler = new $className($modx, $scriptProperties);\n        if (get_class($handler) == $className) {\n            $handler->run();\n        } else {\n            $modx->log(xPDO::LOG_LEVEL_ERROR, $className. \' could not be initialized!\', \'\', \'TinyMCE RTE Plugin\');\n        }\n    } else {\n        $modx->log(xPDO::LOG_LEVEL_ERROR, $className. \' was not found!\', \'\', \'TinyMCE RTE Plugin\');\n    }\n}\n\nreturn;', 0, 'a:0:{}', 0, '', 0, ''),
(2, 0, 0, 'FormIt', '', 0, 3, 0, '/**\n * FormIt\n *\n * Copyright 2009-2017 by Sterc <modx@sterc.nl>\n *\n * FormIt is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * FormIt is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * FormIt; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package formit\n */\n/**\n * FormIt plugin\n *\n * @package formit\n */\n\n$formit = $modx->getService(\n    \'formit\',\n    \'FormIt\',\n    $modx->getOption(\'formit.core_path\', null, $modx->getOption(\'core_path\').\'components/formit/\') .\'model/formit/\',\n    array()\n);\n\nif (!($formit instanceof FormIt)) {\n    return;\n}\n\nswitch ($modx->event->name) {\n    case \'OnManagerPageBeforeRender\':\n        // If migration status is false, show migrate alert message bar in manager\n        if (method_exists(\'FormIt\',\'encryptionMigrationStatus\')) {\n            if (!$formit->encryptionMigrationStatus()) {\n                $modx->lexicon->load(\'formit:mgr\');\n                $properties = array(\'message\' => $modx->lexicon(\'formit.migrate_alert\'));\n                $chunk = $formit->_getTplChunk(\'migrate/alert\');\n                if ($chunk) {\n                    $modx->regClientStartupHTMLBlock($chunk->process($properties));\n                    $modx->regClientCSS($formit->config[\'cssUrl\'] . \'migrate.css\');\n                }\n            }\n        }\n}', 0, 'a:0:{}', 0, '', 0, ''),
(3, 0, 0, 'MIGX', '', 0, 4, 0, '$corePath = $modx->getOption(\'migx.core_path\',null,$modx->getOption(\'core_path\').\'components/migx/\');\n$assetsUrl = $modx->getOption(\'migx.assets_url\', null, $modx->getOption(\'assets_url\') . \'components/migx/\');\nswitch ($modx->event->name) {\n    case \'OnTVInputRenderList\':\n        $modx->event->output($corePath.\'elements/tv/input/\');\n        break;\n    case \'OnTVInputPropertiesList\':\n        $modx->event->output($corePath.\'elements/tv/inputoptions/\');\n        break;\n\n        case \'OnDocFormPrerender\':\n        $modx->controller->addCss($assetsUrl.\'css/mgr.css\');\n        break; \n \n    /*          \n    case \'OnTVOutputRenderList\':\n        $modx->event->output($corePath.\'elements/tv/output/\');\n        break;\n    case \'OnTVOutputRenderPropertiesList\':\n        $modx->event->output($corePath.\'elements/tv/properties/\');\n        break;\n    \n    case \'OnDocFormPrerender\':\n        $assetsUrl = $modx->getOption(\'colorpicker.assets_url\',null,$modx->getOption(\'assets_url\').\'components/colorpicker/\'); \n        $modx->regClientStartupHTMLBlock(\'<script type=\"text/javascript\">\n        Ext.onReady(function() {\n            \n        });\n        </script>\');\n        $modx->regClientStartupScript($assetsUrl.\'sources/ColorPicker.js\');\n        $modx->regClientStartupScript($assetsUrl.\'sources/ColorMenu.js\');\n        $modx->regClientStartupScript($assetsUrl.\'sources/ColorPickerField.js\');		\n        $modx->regClientCSS($assetsUrl.\'resources/css/colorpicker.css\');\n        break;\n     */\n}\nreturn;', 0, 'a:0:{}', 0, '', 0, ''),
(4, 0, 0, 'MIGXquip', '', 0, 4, 0, '$quipCorePath = $modx->getOption(\'quip.core_path\', null, $modx->getOption(\'core_path\') . \'components/quip/\');\n//$assetsUrl = $modx->getOption(\'migx.assets_url\', null, $modx->getOption(\'assets_url\') . \'components/migx/\');\nswitch ($modx->event->name)\n{\n\n    case \'OnDocFormPrerender\':\n\n        \n        require_once $quipCorePath . \'model/quip/quip.class.php\';\n        $modx->quip = new Quip($modx);\n\n        $modx->lexicon->load(\'quip:default\');\n        $quipconfig = $modx->toJson($modx->quip->config);\n        \n        $js = \"\n        Quip.config = Ext.util.JSON.decode(\'{$quipconfig}\');\n        console.log(Quip);\";\n\n        //$modx->controller->addCss($assetsUrl . \'css/mgr.css\');\n        $modx->controller->addJavascript($modx->quip->config[\'jsUrl\'].\'quip.js\');\n        $modx->controller->addHtml(\'<script type=\"text/javascript\">\' . $js . \'</script>\');\n        break;\n\n}\nreturn;', 0, 'a:0:{}', 1, '', 0, ''),
(5, 0, 0, 'migxResizeOnUpload', '', 0, 4, 0, '/**\n * migxResizeOnUpload Plugin\n *\n * Events: OnFileManagerUpload\n * Author: Bruno Perner <b.perner@gmx.de>\n * Modified to read multiple configs from mediasource-property\n * \n * First Author: Vasiliy Naumkin <bezumkin@yandex.ru>\n * Required: PhpThumbOf snippet for resizing images\n * \n * Example: mediasource - property \'resizeConfig\':\n * [{\"alias\":\"origin\",\"w\":\"500\",\"h\":\"500\",\"far\":1},{\"alias\":\"thumb\",\"w\":\"150\",\"h\":\"150\",\"far\":1}]\n */\n\nif ($modx->event->name != \'OnFileManagerUpload\') {\n    return;\n}\n\n\n$file = $modx->event->params[\'files\'][\'file\'];\n$directory = $modx->event->params[\'directory\'];\n\nif ($file[\'error\'] != 0) {\n    return;\n}\n\n$name = $file[\'name\'];\n//$extensions = explode(\',\', $modx->getOption(\'upload_images\'));\n\n$source = $modx->event->params[\'source\'];\n\nif ($source instanceof modMediaSource) {\n    //$dirTree = $modx->getOption(\'dirtree\', $_REQUEST, \'\');\n    //$modx->setPlaceholder(\'docid\', $resource_id);\n    $source->initialize();\n    $basePath = str_replace(\'/./\', \'/\', $source->getBasePath());\n    //$cachepath = $cachepath . $dirTree;\n    $baseUrl = $modx->getOption(\'site_url\') . $source->getBaseUrl();\n    //$baseUrl = $baseUrl . $dirTree;\n    $sourceProperties = $source->getPropertyList();\n\n    //echo \'<pre>\' . print_r($sourceProperties, 1) . \'</pre>\';\n    //$allowedExtensions = $modx->getOption(\'allowedFileTypes\', $sourceProperties, \'\');\n    //$allowedExtensions = empty($allowedExtensions) ? \'jpg,jpeg,png,gif\' : $allowedExtensions;\n    //$maxFilesizeMb = $modx->getOption(\'maxFilesizeMb\', $sourceProperties, \'8\');\n    //$maxFiles = $modx->getOption(\'maxFiles\', $sourceProperties, \'0\');\n    //$thumbX = $modx->getOption(\'thumbX\', $sourceProperties, \'100\');\n    //$thumbY = $modx->getOption(\'thumbY\', $sourceProperties, \'100\');\n    $resizeConfigs = $modx->getOption(\'resizeConfigs\', $sourceProperties, \'\');\n    $resizeConfigs = $modx->fromJson($resizeConfigs);\n    $thumbscontainer = $modx->getOption(\'thumbscontainer\', $sourceProperties, \'thumbs/\');\n    $imageExtensions = $modx->getOption(\'imageExtensions\', $sourceProperties, \'jpg,jpeg,png,gif,JPG\');\n    $imageExtensions = explode(\',\', $imageExtensions);\n    //$uniqueFilenames = $modx->getOption(\'uniqueFilenames\', $sourceProperties, false);\n    //$onImageUpload = $modx->getOption(\'onImageUpload\', $sourceProperties, \'\');\n    //$onImageRemove = $modx->getOption(\'onImageRemove\', $sourceProperties, \'\');\n    $cleanalias = $modx->getOption(\'cleanFilename\', $sourceProperties, false);\n\n}\n\nif (is_array($resizeConfigs) && count($resizeConfigs) > 0) {\n    foreach ($resizeConfigs as $rc) {\n        if (isset($rc[\'alias\'])) {\n            $filePath = $basePath . $directory;\n            $filePath = str_replace(\'//\',\'/\',$filePath);\n            if ($rc[\'alias\'] == \'origin\') {\n                $thumbPath = $filePath;\n            } else {\n                $thumbPath = $filePath . $rc[\'alias\'] . \'/\';\n                $permissions = octdec(\'0\' . (int)($modx->getOption(\'new_folder_permissions\', null, \'755\', true)));\n                if (!@mkdir($thumbPath, $permissions, true)) {\n                    $modx->log(MODX_LOG_LEVEL_ERROR, sprintf(\'[migxResourceMediaPath]: could not create directory %s).\', $thumbPath));\n                } else {\n                    chmod($thumbPath, $permissions);\n                }\n\n            }\n\n\n            $filename = $filePath . $name;\n            $thumbname = $thumbPath . $name;\n            $ext = substr(strrchr($name, \'.\'), 1);\n            if (in_array($ext, $imageExtensions)) {\n                $sizes = getimagesize($filename);\n                echo $sizes[0]; \n                //$format = substr($sizes[\'mime\'], 6);\n                if ($sizes[0] > $rc[\'w\'] || $sizes[1] > $rc[\'h\']) {\n                    if ($sizes[0] < $rc[\'w\']) {\n                        $rc[\'w\'] = $sizes[0];\n                    }\n                    if ($sizes[1] < $rc[\'h\']) {\n                        $rc[\'h\'] = $sizes[1];\n                    }\n                    $type = $sizes[0] > $sizes[1] ? \'landscape\' : \'portrait\';\n                    if (isset($rc[\'far\']) && $rc[\'far\'] == \'1\' && isset($rc[\'w\']) && isset($rc[\'h\'])) {\n                        if ($type = \'landscape\') {\n                            unset($rc[\'h\']);\n                        }else {\n                            unset($rc[\'w\']);\n                        }\n                    }\n\n                    $options = \'\';\n                    foreach ($rc as $k => $v) {\n                        if ($k != \'alias\') {\n                            $options .= \'&\' . $k . \'=\' . $v;\n                        }\n                    }\n                    $resized = $modx->runSnippet(\'phpthumbof\', array(\'input\' => $filePath . $name, \'options\' => $options));\n                    rename(MODX_BASE_PATH . substr($resized, 1), $thumbname);\n                }\n            }\n\n\n        }\n    }\n}', 0, 'a:0:{}', 0, '', 0, ''),
(6, 1, 0, 'pdoTools', '', 0, 6, 0, '/** @var modX $modx */\r\nswitch ($modx->event->name) {\r\n\r\n    case \'OnMODXInit\':\r\n        $fqn = $modx->getOption(\'pdoTools.class\', null, \'pdotools.pdotools\', true);\r\n        $path = $modx->getOption(\'pdotools_class_path\', null, MODX_CORE_PATH . \'components/pdotools/model/\', true);\r\n        $modx->loadClass($fqn, $path, false, true);\r\n\r\n        $fqn = $modx->getOption(\'pdoFetch.class\', null, \'pdotools.pdofetch\', true);\r\n        $path = $modx->getOption(\'pdofetch_class_path\', null, MODX_CORE_PATH . \'components/pdotools/model/\', true);\r\n        $modx->loadClass($fqn, $path, false, true);\r\n        break;\r\n\r\n    case \'OnSiteRefresh\':\r\n        /** @var pdoTools $pdoTools */\r\n        if ($pdoTools = $modx->getService(\'pdoTools\')) {\r\n            if ($pdoTools->clearFileCache()) {\r\n                $modx->log(modX::LOG_LEVEL_INFO, $modx->lexicon(\'refresh_default\') . \': pdoTools\');\r\n            }\r\n        }\r\n        break;\r\n\r\n    case \'OnWebPagePrerender\':\r\n        $parser = $modx->getParser();\r\n        if ($parser instanceof pdoParser) {\r\n            foreach ($parser->pdoTools->ignores as $key => $val) {\r\n                $modx->resource->_output = str_replace($key, $val, $modx->resource->_output);\r\n            }\r\n        }\r\n        break;\r\n}', 0, NULL, 0, '', 0, 'core/components/pdotools/elements/plugins/plugin.pdotools.php'),
(7, 1, 0, 'frontendmanager', '', 0, 7, 0, 'if (!$modx->user->hasSessionContext(\'mgr\') || !$modx->user->isMember(\'Administrator\')) return;\nswitch ($modx->event->name) {\n    case \'OnWebPagePrerender\':\n		if (!$modx->resource->get(\'template\')) break;\n        $frontendManager = $modx->getService(\'frontendmanager\',\'frontendManager\', MODX_CORE_PATH . \'components/frontendmanager/model/frontendmanager/\', array());\n        if(!$frontendManager) return;\n		$contentTypes = explode(\',\', $modx->getOption(\'frontendmanager_contenttypes\'));\n        if (in_array($modx->resource->content_type, $contentTypes)) {\n			$modx->resource->_output .=  $frontendManager->initialize($modx->context->key);\n        }\n        break;\n    case \'OnBeforeManagerPageInit\':\n        if ($_GET[\'frame\']) {\n			$modx->regClientCSS(MODX_ASSETS_URL.\'components/frontendmanager/css/mgr/\'.$modx->getOption(\'frontendmanager_manager_css\', NULL, \'manager.css\'));\n			$modx->regClientStartupScript(MODX_ASSETS_URL.\'components/frontendmanager/js/mgr/\'.$modx->getOption(\'frontendmanager_manager_js\', NULL, \'manager.js\'));\n        }\n        break;\n    default:\n        break;\n}\nreturn;', 0, NULL, 0, '', 0, 'core/components/frontendmanager/elements/plugins/plugin.frontendmanager.php'),
(8, 1, 0, 'AdminTools', '', 0, 8, 0, '/** @var array $scriptProperties */\n$path = $modx->getOption(\'admintools_core_path\', null, $modx->getOption(\'core_path\') . \'components/admintools/\') . \'services/\';\n/** @var AdminTools $AdminTools */\n$AdminTools = $modx->getService(\'admintools\', \'AdminTools\', $path);\n$elementType = null;\nif ($AdminTools instanceof AdminTools) {\n    switch ($modx->event->name) {\n        case \'OnManagerPageBeforeRender\':\n            if ($modx->user->id) {\n                $AdminTools->initialize();\n            }\n            break;\n        case \'OnManagerPageAfterRender\':\n            if ($AdminTools->isLocked()) {\n                $controller->content = $modx->getChunk(\'tpl.lockScreen\', [\n                        \'username\' => $modx->user->username,\n                        \'photo\' => $modx->user->getPhoto(),\n                        \'title\' => $modx->getOption(\'site_name\'),\n                        \'lang\' => $modx->getOption(\'manager_language\'),\n                        \'form_action\' => $AdminTools->getOption(\'connectorUrl\'),\n                        \'auth\' => $modx->user->getUserToken(\'mgr\'),\n                        \'assets_url\' => MODX_ASSETS_URL,\n                        \'input_placeholder\' => $AdminTools->getInputPlaceholder(),\n                    ]\n                );\n            }\n            break;\n        case \'OnDocFormSave\':\n            if ($modx->getOption(\'admintools_clear_only_resource_cache\', null, false) && $modx->event->params[\'mode\'] === modSystemEvent::MODE_UPD) {\n                if ($resource->get(\'syncsite\')) {\n                    $AdminTools->clearResourceCache($resource);\n                }\n                if (!empty($_POST[\'createCache\'])) {\n                    $AdminTools->createResourceCache($resource->uri);\n                }\n            }\n            break;\n        case \'OnManagerPageInit\':\n            if (!$modx->user->isAuthenticated(\'mgr\') && $modx->getOption(\'admintools_email_authorization\', null, false)) {\n                $id = (int) $modx->getOption(\'admintools_loginform_resource\');\n                if (!empty($id) && $modx->getCount(\'modResource\', [\'id\' => $id, \'published\' => 1, \'deleted\' => 0])) {\n                    $url = $modx->makeUrl($id, \'\', \'\', \'full\');\n                    $modx->setOption(\'manager_login_url_alternate\', $url);\n                }\n            }\n            break;\n        case \'OnManagerAuthentication\':\n            if ($modx->getOption(\'admintools_user_can_login\', null, false)) {\n                $modx->setOption(\'admintools_user_can_login\', false);\n                $modx->event->output(true);\n            }\n            break;\n        case \'OnLoadWebDocument\':\n            if ((!$modx->user->active || $modx->user->Profile->blocked) && $modx->user->isAuthenticated($modx->context->get(\'key\'))) {\n                $modx->runProcessor(\'security/logout\');\n            }\n            if ($modx->getOption(\'admintools_alternative_permissions\', null, false) && !$AdminTools->hasPermissions()){\n                $modx->sendUnauthorizedPage();\n            }\n            break;\n        case \'OnTempFormPrerender\':\n            if ($modx->getOption(\'admintools_template_resource_relationship\', null, true)) {\n                $modx->controller->addLastJavascript($AdminTools->getOption(\'jsUrl\') . \'mgr/templates.js\');\n            }\n            break;\n        case \'OnDocFormPrerender\':\n            $_html = [];\n            $output = \'\';\n            if ($modx->getOption(\'admintools_template_resource_relationship\', null, true)) {\n                $_html[\'tpl_res_relationship\'] = \'\n            var tmpl = Ext.getCmp(\"modx-resource-template\");\n            if (tmpl.getValue()) tmpl.label.update(_(\"resource_template\") + \"&nbsp;&nbsp;<a href=\\\"?a=element/template/update&id=\" + tmpl.getValue() + \"\\\"><i class=\\\"icon icon-external-link\\\"></i></a>\");\';\n            }\n            if ($modx->getOption(\'admintools_clear_only_resource_cache\', null, true) && $modx->event->params[\'mode\'] != modSystemEvent::MODE_NEW) {\n                $_html[\'create_resource_cache\'] = \'\n            var cb = Ext.create({\n                xtype: \"xcheckbox\",\n                boxLabel: _(\"admintools_create_resource_cache\"),\n                description: _(\"admintools_create_resource_cache_help\"),\n                hideLabel: true,\n                name: \"createCache\",\n                id: \"createCache\",\n                checked: \'. (int)$modx->getOption(\'admintools_create_resource_cache\', null, false) .\'\n            });\n            if (Ext.getCmp(\"modx-page-settings-right-box-right\")) {\n                Ext.getCmp(\"modx-page-settings-right-box-right\").insert(2,cb);\n                Ext.getCmp(\"modx-page-settings-right-box-left\").add(Ext.getCmp(\"modx-resource-uri-override\"));\n                Ext.getCmp(\"modx-panel-resource\").on(\"success\", function(o){\n                    if (o.result.object.createCache != 0) {\n                        cb.setValue(true);\n                    }\n                });\n            }\';\n            }\n            if (!empty($_html)) {\n            $output .= \'\n    Ext.onReady(function() {\n        setTimeout(function(){\' . implode(\"\\n\", $_html) . \'\n        }, 200);\n    });\';\n            }\n            if ($modx->getOption(\'admintools_alternative_permissions\', null, true) && $modx->hasPermission(\'access_permissions\')) {\n                $modx->controller->addLastJavascript($AdminTools->getOption(\'jsUrl\') . \'mgr/permissions.js\');\n                $output .= \'\n    Ext.ComponentMgr.onAvailable(\"modx-resource-tabs\", function() {\n		this.on(\"beforerender\", function() {\n			this.add({\n				title: _(\"admintools_permissions\"),\n				border: false,\n				items: [{\n					layout: \"anchor\",\n					border: false,\n					items: [{\n						html: _(\"admintools_permissions_desc\"),\n						border: false,\n						bodyCssClass: \"panel-desc\"\n					}, {\n						xtype: \"admintools-grid-permissions\",\n						anchor: \"100%\",\n						cls: \"main-wrapper\",\n						resource: \' . $id . \'\n					}]\n				}]\n			});\n		});\n	});\n\';\n            }\n            if (!empty($output)) {\n                $modx->controller->addHtml(\'<script>\' . $output . \'</script>\');\n            }\n            break;\n        case \'OnMODXInit\':\n            if (($modx->context->get(\'key\') !== \'mgr\')\n                && $modx->getOption(\'admintools_only_current_context_user\', null, false)\n                && $modx->user->isAuthenticated(\'mgr\')\n                && !$modx->user->isAuthenticated($modx->context->get(\'key\')))\n            {\n               $modx->user = $modx->newObject(\'modUser\');\n                $modx->user->fromArray([\'id\' => 0, \'username\' => $modx->getOption(\'default_username\', \'\', \'(anonymous)\', true)], \'\', true);\n            }\n            break;\n    }\n}', 0, NULL, 0, '', 0, 'core/components/admintools/elements/plugins/plugin.admintools.php'),
(9, 0, 0, 'mixedImage', 'mixedImage 2.1.0-pl plugin for MODx Revolution', 0, 0, 0, '$corePath = $modx->getOption(\'core_path\',null,MODX_CORE_PATH).\'components/mixedimage/\';\n$assetsUrl = $modx->getOption(\'assets_url\',null,MODX_ASSETS_URL).\'components/mixedimage/\';\n\n$modx->lexicon->load(\'mixedimage:default\');\n\nswitch ($modx->event->name) {\n    case \'OnTVInputRenderList\':\n        $modx->event->output($corePath.\'elements/tv/input/\');\n        break;\n    case \'OnTVOutputRenderList\':\n        $modx->event->output($corePath.\'elements/tv/output/\');\n        break;\n    case \'OnTVInputPropertiesList\':\n        $modx->event->output($corePath.\'elements/tv/input/options/\');\n        break;\n    case \'OnTVOutputRenderPropertiesList\':\n        $modx->event->output($corePath.\'elements/tv/properties/\');\n        break;\n    case \'OnDocFormPrerender\':\n    case \'OnManagerPageBeforeRender\':\n        $modx->regClientStartupScript($assetsUrl.\'js/mgr/mixedimage.js\');\n        $modx->regClientCSS($assetsUrl.\'css/mgr/mixedimage.css\');\n        $modx->controller->addLexiconTopic(\'mixedimage:default\');\n        break;\n    case \'OnMODXInit\':\n    case \'OnLoadWebDocument\':\n        $mTypes = $modx->getOption(\'manipulatable_url_tv_output_types\',null,\'image,file\').\',mixedimage\';\n        $modx->setOption(\'manipulatable_url_tv_output_types\', $mTypes);\n        break;\n}', 0, NULL, 0, '', 0, ''),
(10, 0, 0, 'Ace', 'Ace code editor plugin for MODx Revolution', 0, 0, 0, '/**\n * Ace Source Editor Plugin\n *\n * Events: OnManagerPageBeforeRender, OnRichTextEditorRegister, OnSnipFormPrerender,\n * OnTempFormPrerender, OnChunkFormPrerender, OnPluginFormPrerender,\n * OnFileCreateFormPrerender, OnFileEditFormPrerender, OnDocFormPrerender\n *\n * @author Danil Kostin <danya.postfactum(at)gmail.com>\n *\n * @package ace\n *\n * @var array $scriptProperties\n * @var Ace $ace\n */\nif ($modx->event->name == \'OnRichTextEditorRegister\') {\n    $modx->event->output(\'Ace\');\n    return;\n}\n\nif ($modx->getOption(\'which_element_editor\', null, \'Ace\') !== \'Ace\') {\n    return;\n}\n\n$corePath = $modx->getOption(\'ace.core_path\', null, $modx->getOption(\'core_path\').\'components/ace/\');\n$ace = $modx->getService(\'ace\', \'Ace\', $corePath.\'model/ace/\');\n$ace->initialize();\n\n$extensionMap = array(\n    \'tpl\'   => \'text/x-smarty\',\n    \'htm\'   => \'text/html\',\n    \'html\'  => \'text/html\',\n    \'css\'   => \'text/css\',\n    \'scss\'  => \'text/x-scss\',\n    \'less\'  => \'text/x-less\',\n    \'svg\'   => \'image/svg+xml\',\n    \'xml\'   => \'application/xml\',\n    \'xsl\'   => \'application/xml\',\n    \'js\'    => \'application/javascript\',\n    \'json\'  => \'application/json\',\n    \'php\'   => \'application/x-php\',\n    \'sql\'   => \'text/x-sql\',\n    \'md\'    => \'text/x-markdown\',\n    \'txt\'   => \'text/plain\',\n    \'twig\'  => \'text/x-twig\'\n);\n\n// Define default mime for html elements(templates, chunks and html resources)\n$html_elements_mime=$modx->getOption(\'ace.html_elements_mime\',null,false);\nif(!$html_elements_mime){\n    // this may deprecated in future because components may set ace.html_elements_mime option now\n    switch (true) {\n        case $modx->getOption(\'twiggy_class\'):\n            $html_elements_mime = \'text/x-twig\';\n            break;\n        case $modx->getOption(\'pdotools_fenom_parser\'):\n            $html_elements_mime = \'text/x-smarty\';\n            break;\n        default:\n            $html_elements_mime = \'text/html\';\n    }\n}\n\n// Defines wether we should highlight modx tags\n$modxTags = false;\nswitch ($modx->event->name) {\n    case \'OnSnipFormPrerender\':\n        $field = \'modx-snippet-snippet\';\n        $mimeType = \'application/x-php\';\n        break;\n    case \'OnTempFormPrerender\':\n        $field = \'modx-template-content\';\n        $modxTags = true;\n        $mimeType = $html_elements_mime;\n        break;\n    case \'OnChunkFormPrerender\':\n        $field = \'modx-chunk-snippet\';\n        if ($modx->controller->chunk && $modx->controller->chunk->isStatic()) {\n            $extension = pathinfo($modx->controller->chunk->name, PATHINFO_EXTENSION);\n            if(!$extension||!isset($extensionMap[$extension])){\n                $extension = pathinfo($modx->controller->chunk->getSourceFile(), PATHINFO_EXTENSION);\n            }\n            $mimeType = isset($extensionMap[$extension]) ? $extensionMap[$extension] : \'text/plain\';\n        } else {\n            $mimeType = $html_elements_mime;\n        }\n        $modxTags = true;\n        break;\n    case \'OnPluginFormPrerender\':\n        $field = \'modx-plugin-plugincode\';\n        $mimeType = \'application/x-php\';\n        break;\n    case \'OnFileCreateFormPrerender\':\n        $field = \'modx-file-content\';\n        $mimeType = \'text/plain\';\n        break;\n    case \'OnFileEditFormPrerender\':\n        $field = \'modx-file-content\';\n        $extension = pathinfo($scriptProperties[\'file\'], PATHINFO_EXTENSION);\n        $mimeType = isset($extensionMap[$extension])\n            ? $extensionMap[$extension]\n            : (\'@FILE:\'.pathinfo($scriptProperties[\'file\'], PATHINFO_BASENAME));\n        $modxTags = $extension == \'tpl\';\n        break;\n    case \'OnDocFormPrerender\':\n        if (!$modx->controller->resourceArray) {\n            return;\n        }\n        $field = \'ta\';\n        $mimeType = $modx->getObject(\'modContentType\', $modx->controller->resourceArray[\'content_type\'])->get(\'mime_type\');\n\n        if($mimeType == \'text/html\')$mimeType = $html_elements_mime;\n\n        if ($modx->getOption(\'use_editor\')){\n            $richText = $modx->controller->resourceArray[\'richtext\'];\n            $classKey = $modx->controller->resourceArray[\'class_key\'];\n            if ($richText || in_array($classKey, array(\'modStaticResource\',\'modSymLink\',\'modWebLink\',\'modXMLRPCResource\'))) {\n                $field = false;\n            }\n        }\n        $modxTags = true;\n        break;\n    case \'OnTVInputRenderList\':\n        $modx->event->output($corePath . \'elements/tv/input/\');\n        break;\n    default:\n        return;\n}\n\n$modxTags = (int) $modxTags;\n$script = \'\';\nif ($field) {\n    $script .= \"MODx.ux.Ace.replaceComponent(\'$field\', \'$mimeType\', $modxTags);\";\n}\n\nif ($modx->event->name == \'OnDocFormPrerender\' && !$modx->getOption(\'use_editor\')) {\n    $script .= \"MODx.ux.Ace.replaceTextAreas(Ext.query(\'.modx-richtext\'));\";\n}\n\nif ($script) {\n    $modx->controller->addHtml(\'<script>Ext.onReady(function() {\' . $script . \'});</script>\');\n}', 0, NULL, 0, '', 0, 'ace/elements/plugins/ace.plugin.php'),
(11, 0, 0, 'Babel', 'Links and synchronizes multilingual resources.', 0, 10, 0, '/**\n * Babel\n *\n * Copyright 2010 by Jakob Class <jakob.class@class-zec.de>\n *\n * This file is part of Babel.\n *\n * Babel is free software; you can redistribute it and/or modify it under the\n * terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * Babel is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Babel; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package babel\n */\n/**\n * Babel Plugin to link and synchronize multilingual resources\n *\n * Based on ideas of Sylvain Aerni <enzyms@gmail.com>\n *\n * Events:\n * OnDocFormPrerender,OnDocFormSave,OnEmptyTrash,OnContextRemove,OnResourceDuplicate\n *\n * @author Jakob Class <jakob.class@class-zec.de>\n *         goldsky <goldsky@virtudraft.com>\n *\n * @package babel\n *\n */\n$babel = $modx->getService(\'babel\', \'Babel\', $modx->getOption(\'babel.core_path\', null, $modx->getOption(\'core_path\').\'components/babel/\').\'model/babel/\');\n\n/* be sure babel TV is loaded */\nif (!($babel instanceof Babel) || !$babel->babelTv)\n    return;\n\nswitch ($modx->event->name) {\n    case \'OnDocFormPrerender\':\n        $output       = \'\';\n        $errorMessage = \'\';\n        $resource     = & $modx->event->params[\'resource\'];\n        if (!$resource) {\n            /* a new resource is being to created\n             * -> skip rendering the babel box */\n            break;\n        }\n        $linkedResources = $babel->getLinkedResources($resource->get(\'id\'));\n        if (empty($linkedResources)) {\n            /* always be sure that the Babel TV is set */\n            $babel->initBabelTv($resource);\n        }\n\n        /* create babel-box with links to translations */\n        $outputLanguageItems = \'\';\n        if (!$modx->lexicon) {\n            $modx->getService(\'lexicon\', \'modLexicon\');\n        }\n        $languagesStore = [];\n        $contextKeys    = $babel->getGroupContextKeys($resource->get(\'context_key\'));\n        foreach ($contextKeys as $contextKey) {\n            /* for each (valid/existing) context of the context group a button will be displayed */\n            $context = $modx->getObject(\'modContext\', [\'key\' => $contextKey]);\n            if (!$context) {\n                $modx->log(modX::LOG_LEVEL_ERROR, \'Could not load context: \'.$contextKey);\n                continue;\n            }\n            $context->prepare();\n            $cultureKey       = $context->getOption(\'cultureKey\', $modx->getOption(\'cultureKey\'));\n            $languagesStore[] = [$modx->lexicon(\'babel.language_\'.$cultureKey).\" ($contextKey)\", $contextKey];\n        }\n\n        $babel->config[\'context_key\']    = $resource->get(\'context_key\');\n        $babel->config[\'languagesStore\'] = $languagesStore;\n        $babel->config[\'menu\']           = $babel->getMenu($resource);\n        if (empty($babel->config[\'menu\'])) {\n            $modx->log(modX::LOG_LEVEL_ERROR, \'[Babel] Could not load menu for context key: \"\'.$babel->config[\'context_key\'].\'\". Try to check \"babel.contextKeys\" in System Settings. If this is intended, you can ignore this warning.\');\n            return;\n        }\n        $version         = str_replace(\' \', \'\', $babel->config[\'version\']);\n        $isCSSCompressed = $modx->getOption(\'compress_css\');\n        $withVersion     = $isCSSCompressed ? \'\' : \'?v=\'.$version;\n        $modx->controller->addCss($babel->config[\'cssUrl\'].\'babel.css\'.$withVersion);\n\n        $modx->controller->addLexiconTopic(\'babel:default\');\n        $isJsCompressed = $modx->getOption(\'compress_js\');\n        $withVersion    = $isJsCompressed ? \'\' : \'?v=\'.$version;\n        $modx->controller->addJavascript($babel->config[\'jsUrl\'].\'babel.class.js\'.$withVersion);\n        $modx->controller->addHtml(\'\n<script type=\"text/javascript\">\n    Ext.onReady(function () {\n        var babel = new Babel(\'.json_encode($babel->config).\');\n        babel.getMenu(babel.config.menu);\n    });\n</script>\');\n        break;\n\n    case \'OnDocFormSave\':\n        $resource = & $modx->event->params[\'resource\'];\n        if (!$resource) {\n            $modx->log(modX::LOG_LEVEL_ERROR, \'No resource provided for OnDocFormSave event\');\n            break;\n        }\n        if ($modx->event->params[\'mode\'] == modSystemEvent::MODE_NEW) {\n            /* no TV synchronization for new resources, just init Babel TV */\n            $babel->initBabelTv($resource);\n            break;\n        }\n        $babel->synchronizeTvs($resource->get(\'id\'));\n        break;\n\n    case \'OnEmptyTrash\':\n        /* remove translation links to non-existing resources */\n        $deletedResourceIds = & $modx->event->params[\'ids\'];\n        if (is_array($deletedResourceIds)) {\n            foreach ($deletedResourceIds as $deletedResourceId) {\n                $babel->removeLanguageLinksToResource($deletedResourceId);\n            }\n        }\n        break;\n\n    case \'OnContextRemove\':\n        /* remove translation links to non-existing contexts */\n        $context = & $modx->event->params[\'context\'];\n        if ($context) {\n            $babel->removeLanguageLinksToContext($context->get(\'key\'));\n        }\n        break;\n\n    case \'OnResourceDuplicate\':\n        /* init Babel TV of duplicated resources */\n        $resource = & $modx->event->params[\'newResource\'];\n        $babel->initBabelTvsRecursive($modx, $babel, $resource->get(\'id\'));\n        break;\n\n    case \'OnResourceSort\':\n        $nodesAffected = & $modx->event->params[\'nodesAffected\'];\n        foreach ($nodesAffected as $node) {\n            $linkedResources = $babel->getLinkedResources($node->get(\'id\'));\n            foreach ($linkedResources as $key => $id) {\n                if ($id === $node->get(\'id\')) {\n                    unset($linkedResources[$key]);\n                }\n            }\n            $linkedResources[$node->get(\'context_key\')] = $node->get(\'id\');\n            $babel->updateBabelTv($linkedResources, $linkedResources);\n        }\n\n        break;\n\n    case \'OnSiteRefresh\':\n        $cacheManager = $modx->getCacheManager();\n        $cacheManager->refresh([\n            \'babel\' => [],\n                               ]);\n        break;\n\n    default:\n        break;\n}\nreturn;', 0, 'a:0:{}', 0, '', 0, ''),
(12, 0, 0, 'SEO Suite', 'Plugin to handle the SEO Suite redirects.', 0, 13, 0, '/**\n * SeoSuite\n *\n * Copyright 2019 by Sterc <modx@sterc.com>\n */\n$instance = $modx->getService(\'seosuite\', \'SeoSuite\', $modx->getOption(\'seosuite.core_path\', null, $modx->getOption(\'core_path\') . \'components/seosuite/\') . \'model/seosuite/\');\nif ($instance instanceof SeoSuite) {\n    $instance->firePlugins($modx->event, $scriptProperties);\n}', 0, 'a:0:{}', 0, '', 0, ''),
(13, 1, 0, 'localizator', '', 0, 16, 0, '/* @var modX $modx */\n/* @var localizator $localizator */\n$localizator = $modx->getService(\'localizator\');\nswitch($modx->event->name) {\n    case \'OnTVFormPrerender\':\n        $modx->controller->addLexiconTopic(\'localizator:default\');\n        $modx->controller->addHtml(\'\n            <script type=\"text/javascript\">\n                Ext.ComponentMgr.onAvailable(\"modx-panel-tv\", function(config) {\n                    Ext.ComponentMgr.onAvailable(\"modx-tv-form\", function() {\n                        this.items[1].items[1].items.push({\n                            xtype: \"xcheckbox\"\n                            ,boxLabel: _(\"tv_localizator_enabled\")\n                            ,description: _(\"tv_localizator_enabled_msg\")\n                            ,name: \"localizator_enabled\"\n                            ,id: \"modx-tv-localizator_enabled\"\n                            ,inputValue: 1\n                            ,checked: config.record.localizator_enabled || false\n                        });\n                    });\n                });\n            </script>\n            \');\n        break;\n    case \'OnDocFormPrerender\':\n        if ($mode == \'upd\'){\n            $modx->controller->addLexiconTopic(\'localizator:default\');\n            $modx->controller->addCss($localizator->config[\'cssUrl\'] . \'mgr/main.css\');\n            $modx->controller->addCss($localizator->config[\'cssUrl\'] . \'mgr/bootstrap.buttons.css\');\n            $modx->controller->addJavascript($localizator->config[\'jsUrl\'] . \'mgr/localizator.js\');\n            $modx->controller->addJavascript($localizator->config[\'jsUrl\'] . \'mgr/misc/utils.js\');\n            $modx->controller->addJavascript($localizator->config[\'jsUrl\'] . \'mgr/misc/combo.js\');\n            $modx->controller->addJavascript($localizator->config[\'jsUrl\'] . \'mgr/widgets/content.grid.js\');\n            $modx->controller->addHtml(\'\n            <script type=\"text/javascript\">\n                localizator.config = \' . json_encode($localizator->config) . \';\n                localizator.config.connector_url = \"\' . $localizator->config[\'connectorUrl\'] . \'\";\n                localizator.config.resource_template = \"\' . $resource->get(\'template\') . \'\";\n                Ext.ComponentMgr.onAvailable(\"modx-resource-tabs\", function() {\n                    this.on(\"beforerender\", function() {\n                        this.add({\n                            title: _(\"localizator_tab\"),\n                            id: \"localizator-resource-tab\",\n                            items: [{\n                                xtype: \"localizator-grid-content\",\n                                cls: \"main-wrapper\",\n                                resource_id: \' . $id . \',\n                            }]\n                        });\n                    });\n                });\n            </script>\n            \');\n        }\n        break;\n\n    case \'OnMODXInit\':\n        $include = include_once($localizator->config[\'modelPath\'] . \'localizator/plugin.mysql.inc.php\');\n        if (is_array($include)) {\n            foreach ($include as $class => $map){\n                if (!isset($modx->map[$class])) {\n                    $modx->loadClass($class);\n                }\n                if (isset($modx->map[$class])) {\n                    foreach ($map as $key => $values) {\n                        $modx->map[$class][$key] = array_merge($modx->map[$class][$key], $values);\n                    }\n                }\n            }\n        }\n\n        $isAjax = !empty($_SERVER[\'HTTP_X_REQUESTED_WITH\']) && $_SERVER[\'HTTP_X_REQUESTED_WITH\'] == \'XMLHttpRequest\';\n        if ($modx->getOption(\'friendly_urls\') && $isAjax && isset($_SERVER[\'HTTP_REFERER\'])){\n            $referer = parse_url($_SERVER[\'HTTP_REFERER\']);\n            if (stripos($referer[\'path\'], MODX_MANAGER_URL) === 0) return;\n            $localizator->findLocalization($referer[\'host\'], ltrim($referer[\'path\'], \'/\'));\n        }\n        break;\n\n    case \'OnHandleRequest\':\n        if($modx->context->key == \'mgr\' || !$modx->getOption(\'friendly_urls\')) return;\n        $q_var = $modx->getOption(\'request_param_alias\', null, \'q\');\n        $localizator->findLocalization($_SERVER[\'HTTP_HOST\'], $_REQUEST[$q_var]);\n        break;\n\n    case \'OnPageNotFound\':\n        $localizator_key = $modx->localizator_key;\n        $q_var = $modx->getOption(\'request_param_alias\', null, \'q\');\n        $request = &$_REQUEST[$q_var];\n        if($request == $localizator_key) {\n            $modx->sendRedirect($request . \'/\', array(\'responseCode\' => \'HTTP/1.1 301 Moved Permanently\'));\n        } else if (preg_match(\'/^(\'.$localizator_key.\')\\//i\', $request)) {\n            $request = preg_replace(\'/^\'.$localizator_key.\'\\//\', \'\', $request);\n        }\n        $resource_id = (!$request) ? $modx->getOption(\'site_start\', null, 1) : $localizator->findResource($request);\n        if($resource_id) {\n            $modx->sendForward($resource_id);\n        }\n        break;\n\n    case \'OnLoadWebDocument\':\n        $q = $modx->newQuery(\'localizatorContent\');\n        $q->leftJoin(\'localizatorLanguage\',\'localizatorLanguage\', \'localizatorLanguage.key = localizatorContent.key\');\n        $q->where(array(\n            \'localizatorContent.resource_id\' => $modx->resource->id,\n        ));\n        $q->where(array(\n            \'localizatorLanguage.key\' => $modx->localizator_key,\n            \'OR:localizatorLanguage.cultureKey:=\' => $modx->localizator_key,\n        ));\n        $content = $modx->getObject(\'localizatorContent\', $q);\n        if($content) {\n            $placeholders = array();\n            $fields = explode(\',\', $modx->getOption(\'localizator_translate_fields\'));\n            foreach($fields as $field) {\n                $value = $content->get($field);\n                if($field == \'content\') {\n                    $placeholders[\'localizator_content\'] = $value;\n                    $modx->resource->set(\'localizator_content\', $value);\n                } else {\n                    $placeholders[$field] = $value;\n                    $modx->resource->set($field, $value);\n                }\n            }\n            foreach ($content->getTVKeys() as $field){\n                $value = $content->get($field);\n                if (!empty($value)){\n                    $value = localizatorContent::renderTVOutput($modx, $field, $value, $modx->resource->id);\n                    $modx->resource->_fieldMeta[$field] = [\n                        \'dbtype\' => \'mediumtext\',\n                        \'phptype\' => \'string\',\n                    ];\n                    \n                    $placeholders[$field] = $value;\n                    $modx->resource->set($field, $value);\n                }\n            }\n            $modx->setPlaceholders($placeholders, \'*\');\n        }\n        //$modx->resource->cacheable = false;\n        break;\n        \n    case \'OnDocFormSave\':\n        if ($mode == \'new\'){\n            if ($key = $modx->getOption(\'localizator_default_language\', null, false, true)){\n                if ($fields = $modx->getOption(\'localizator_translate_fields\', null, false, true)){\n                    //if (!$content = $modx->getObject(\'localizatorContent\', [\'resource_id\' => $resource->get(\'id\'), \'key\' => $key])){\n                        $content = $modx->newObject(\'localizatorContent\');\n                        $content->set(\'resource_id\', $resource->get(\'id\'));\n                        $content->set(\'key\', $key);\n                    //}\n                    $fields = array_map(\'trim\', explode(\',\', $fields));\n                    foreach ($fields as $field) {\n                        if (isset($resource->_fieldMeta[$field])){\n                            $v = $resource->get($field);\n                            if ($v){\n                                $content->set($field, $v);\n                            }\n                        }\n                    }\n                    foreach ($content->getTVKeys() as $field){\n                        //if (!in_array($field, $fields)) continue;\n                        $v = $resource->getTVValue($field);\n                        if ($v){\n                            $content->set($field, $v);\n                        }\n                    }\n                    $content->save();\n                }\n            }\n        }\n        elseif (in_array($resource->get(\'class_key\'), array(\'modStaticResource\', \'modSymLink\', \'modWebLink\'))){\n            $upd = $modx->prepare(\"UPDATE \".$modx->getTableName(\'localizatorContent\').\" SET `content` = ? WHERE `resource_id` = ?\");\n            $upd->execute(array($resource->get(\'content\'), $resource->get(\'id\')));\n        }\n        break;\n\n    case \'OnEmptyTrash\':\n        if (!empty($ids)){\n            $modx->removeCollection(\'localizatorContent\', array(\'resource_id:IN\' => $ids));\n            $modx->removeCollection(\'locTemplateVarResource\', array(\'contentid:IN\' => $ids));\n        }\n        break;\n\n    case \'mse2OnBeforeSearchIndex\':\n        $keys = $mSearch2->fields;\n        unset($keys[\'comment\']);\n\n        if ($contents = $modx->getCollection(\'localizatorContent\', array(\'resource_id\' => $resource->id))) {\n            foreach ($contents as $content) {\n                foreach ($keys as $k => $v) {\n                    $field = $k;\n                    if (strpos($field, \'tv_\') !== false) {\n                        $field = substr($field, 3);\n                    }\n                    $value = $content->get($field);\n                    // Если поле заполнено\n                    if (!empty($value)) {\n                        $field_key = $content->key . \'-\' . $k;\n                        $mSearch2->fields[$field_key] = $v;\n                        $resource->set($field_key, $value);\n                    }\n                }\n            }\n        }\n        break;\n\n    case \'pdoToolsOnFenomInit\':\n        /** @var Fenom $fenom */\n        $pdo = $modx->getService(\'pdoTools\');\n\n        $fenom->addModifier(\'locfield\', function ($id, $field = null) use ($pdo, $modx) {\n            /** @var modResource $resource */\n            if (empty($id)) {\n                $resource = $modx->resource;\n            } elseif (!is_numeric($id)) {\n                $field = $id;\n                $resource = $modx->resource;\n            } elseif (!$resource = $pdo->getStore($id, \'resource\')) {\n                $resource = $modx->getObject(\'modResource\', $id);\n                $pdo->setStore($id, $resource, \'resource\');\n            }\n\n            if (!$resource)\n                return \'\';\n\n            $id = $resource->get(\'id\');\n            $key = $modx->localizator_key;\n            $output = \'\';\n\n            if (in_array($field, array_diff(array_keys($modx->getFields(\'localizatorContent\')), array(\'id\', \'resource_id\')))){\n                $q = $modx->newQuery(\"localizatorContent\")\n                    ->where(array(\n                        \"resource_id\" => $id,\n                        \"key\" => $key,\n                    ))\n                    ->select($field);\n                if ($q->prepare() && $q->stmt->execute()){\n                    $output = $q->stmt->fetchColumn();\n                }\n            }\n            elseif (in_array($field, array_keys($modx->getFields(\'modResource\')))){\n                $output = $resource->get($field);\n            }\n            elseif ($tv = $modx->getObject(\'modTemplateVar\', array(\'name\' => $field))){\n                if ($tv->get(\'localizator_enabled\')){\n                    $q = $modx->newQuery(\"locTemplateVarResource\")\n                        ->where(array(\n                            \"contentid\" => $id,\n                            \"key\" => $key,\n                            \"tmplvarid\" => $tv->get(\'id\'),\n                        ))\n                        ->select(\'value\');\n                    if ($q->prepare() && $q->stmt->execute()){\n                        if ($output = $q->stmt->fetchColumn()){\n                            $output = localizatorContent::renderTVOutput($modx, $tv, $output, $id);\n                        }\n                    }\n                }\n                else{\n                    $output = $resource->getTVValue($field);\n                }\n            }\n            return $output;\n        });\n        break;\n}', 0, NULL, 1, '', 0, 'core/components/localizator/elements/plugins/plugin.localizator.php');
INSERT INTO `bardar_site_plugins` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `plugincode`, `locked`, `properties`, `disabled`, `moduleguid`, `static`, `static_file`) VALUES
(14, 1, 0, '_modificators', '', 0, 0, 0, '/** @var modX $modx */\nswitch ($modx->event->name) {\n    case \'pdoToolsOnFenomInit\':\n \n        /** @var Fenom $fenom **/\n        $fenom->addModifier(\'phone\', function ($phone) {\n            $phone = str_replace(array(\'_\', \'-\', \'—\',\' \'), \'\', trim($phone));\n            return $phone;\n        }); \n        \n \n        /** @var Fenom $fenom **/\n        $fenom->addModifier(\'dateRU\', function ($date, $format = \'%j %month %Y\', $cultureKey = null)  use ($modx) {\n            if (!is_numeric($date)) {\n                $date = strtotime($date);\n            }\n            if (!$date) {\n                $date = time();\n            } \n            \n            $cultureKey = $modx->getOption(\'cultureKey\'); \n            \n            switch ($cultureKey) {\n                case \'ro\':\n                    $monthes = \'Ian, Feb, Mar, Apr, Mai, Iun, Iul, Aug, Sep, Oct, Noi, Dec\'; \n                    break;\n                case \'en\':\n                    $monthes = \'jan, feb, mar, apr, may, jun, jul, aug, sep, oct nov, dec\';\n                    //$weekdays = \'Monday, Tuesday, Wednesday, Thursday, Friday, Saturday, Sunday\';\n                    break;\n                case \'it\':\n                    $monthes = \'Gennaio, Febbraio, Marzo, Aprile, Maggio, Giugno, Luglio, Agosto, Settembre, Ottobre, Novembre, Dicembre\';\n                    //$weekdays = \'Monday, Tuesday, Wednesday, Thursday, Friday, Saturday, Sunday\';\n                    break; \n                case \'ru\':\n                    $monthes = \'янв, фев, мар, апр, май, июнь, июль, авг, сен, окт, ноя, дек\';\n                    //$weekdays = \'Понедельник, Вторник, Среда, Четверг, Пятница, Суббота, Воскресенье\';\n                    break;\n                default:\n                    $monthes = \'Ian, Feb, Mar, Apr, Mai, Iun, Iul, Aug, Sep, Oct, Noi, Dec\';\n                    break;\n            }\n            \n            $monthes = explode(\',\', $monthes);\n            array_unshift($monthes, \'\');\n            \n         \n            $month = trim($modx->getOption(date(\'n\', $date), $monthes, \'\'));  \n            $day =  strftime(\'%d\', $date);\n            \n            \n            $output =\'<span>\'.$month.\'</span><span>\'.$day.\'</span>\';\n            \n            return $output;\n        });\n        \n        \n        $fenom->addModifier(\'version\', function ($input) use ($modx) {\n            $output = $input;\n            $file = MODX_BASE_PATH . $input;\n            // Если есть файл и время последнего изменения больше чем время изменение текущего файла\n            if (file_exists($file) && filemtime($file) > $output) {\n                $output = filemtime($file);\n                $output = date(\'dmYHi\', $output);\n                $output = $input . \'?v=\' . $output;\n            }\n            return $output;\n        });\n\n \n        \n        \n        break;\n        \n        \n}', 0, 'a:0:{}', 0, '', 0, ''),
(15, 1, 0, 'switchContext', '', 0, 0, 0, '// Работаем только на фронтенде и только с friendly urls\nif ($modx->event->name != \'OnHandleRequest\' || $modx->context->key == \'mgr\' || !$modx->getOption(\'friendly_urls\')) {return;}\n\n// Получаем запрашиваемый url\n$alias = $modx->getOption(\'request_param_alias\', null, \'alias\', true);\n$request = &$_REQUEST[$alias];\n\n// Выбираем контексты с настройкой base_url\n$q = $modx->newQuery(\'modContextSetting\', array(\'key\' => \'base_url\', \'value:!=\' => \'\'));\n$q->select(\'context_key,value\');\n\n$cultureKey = \"ro\";\n$modx->setOption(\'cultureKey\', \'ro\');\n\n$contexts = array();\n$tstart = microtime(true);\n\nif ($q->prepare() && $q->stmt->execute()) {\n	// Учитываем наш запрос в БД\n	$modx->queryTime += microtime(true) - $tstart;\n	$modx->executedQueries++;\n\n	while ($row = $q->stmt->fetch(PDO::FETCH_ASSOC)) {\n		$base_url = trim($row[\'value\'], \'/\');\n		$context = $row[\'context_key\'];\n		\n		// Если запрос начинается с base_url какого-то контекста\n		if (preg_match(\'/^(\'.$base_url.\')\\//i\', $request)) {\n			// То переключаемся на этот контекст\n			// Web инициализируется в index.php - на него переключаться не нужно \n			\n			if ($context != \'web\') {\n				$modx->switchContext($context);\n			}\n			\n			switch($context){\n    		    case \"web\": \n    		        $cultureKey = \"ro\";\n    		        $modx->setOption(\'cultureKey\', \'ro\');\n    		    break;\n    		    case \"ru\": \n    		        $cultureKey = \"ru\";\n    		        $modx->setOption(\'cultureKey\', \'ru\');\n    		    break;\n    		    case \"en\": \n    		        $cultureKey = \"en\";\n    		        $modx->setOption(\'cultureKey\', \'en\');\n    		    break;\n    		    default: \n    		        $cultureKey = \"ro\"; \n    		        $modx->setOption(\'cultureKey\', \'ro\');\n    		}\n			\n			$modx->lexicon->load($cultureKey . \':localizator:site\');\n            $modx->lexicon->load($cultureKey . \':minishop2:default\');  \n			\n			// Вырезаем base_url из запроса, чтобы MODX нашел ресурс по uri\n			$request = preg_replace(\'/^\'.$base_url.\'\\//\', \'\', $request);\n			// Дело сделано - выходим из цикла\n			break;\n		}\n	}\n}\n\n\n$modx->lexicon->load($cultureKey . \':localizator:site\');\n$modx->lexicon->load($cultureKey . \':minishop2:default\');', 0, 'a:0:{}', 0, '', 0, ''),
(16, 1, 0, 'miniShop2', '', 0, 18, 0, '/** @var modX $modx */\nswitch ($modx->event->name) {\n    case \'OnMODXInit\':\n        // Load extensions\n        /** @var miniShop2 $miniShop2 */\n        if ($miniShop2 = $modx->getService(\'miniShop2\')) {\n            $miniShop2->loadMap();\n        }\n        break;\n\n    case \'OnHandleRequest\':\n        // Handle ajax requests\n        $isAjax = !empty($_SERVER[\'HTTP_X_REQUESTED_WITH\']) && $_SERVER[\'HTTP_X_REQUESTED_WITH\'] == \'XMLHttpRequest\';\n        if (empty($_REQUEST[\'ms2_action\']) || !$isAjax) {\n            return;\n        }\n        /** @var miniShop2 $miniShop2 */\n        if ($miniShop2 = $modx->getService(\'miniShop2\')) {\n            $response = $miniShop2->handleRequest($_REQUEST[\'ms2_action\'], @$_POST);\n            @session_write_close();\n            exit($response);\n        }\n        break;\n\n    case \'OnManagerPageBeforeRender\':\n        /** @var miniShop2 $miniShop2 */\n        if ($miniShop2 = $modx->getService(\'miniShop2\')) {\n            $modx->controller->addLexiconTopic(\'minishop2:default\');\n            $modx->regClientStartupScript($miniShop2->config[\'jsUrl\'] . \'mgr/misc/ms2.manager.js\');\n        }\n        break;\n\n    case \'OnLoadWebDocument\':\n        // Handle non-ajax requests\n        if (!empty($_REQUEST[\'ms2_action\'])) {\n            if ($miniShop2 = $modx->getService(\'miniShop2\')) {\n                $miniShop2->handleRequest($_REQUEST[\'ms2_action\'], @$_POST);\n            }\n        }\n        // Set product fields as [[*resource]] tags\n        if ($modx->resource->get(\'class_key\') == \'msProduct\') {\n            if ($dataMeta = $modx->getFieldMeta(\'msProductData\')) {\n                unset($dataMeta[\'id\']);\n                $modx->resource->_fieldMeta = array_merge(\n                    $modx->resource->_fieldMeta,\n                    $dataMeta\n                );\n            }\n        }\n        break;\n\n    case \'OnWebPageInit\':\n        // Set referrer cookie\n        /** @var msCustomerProfile $profile */\n        $referrerVar = $modx->getOption(\'ms2_referrer_code_var\', null, \'msfrom\', true);\n        $cookieVar = $modx->getOption(\'ms2_referrer_cookie_var\', null, \'msreferrer\', true);\n        $cookieTime = $modx->getOption(\'ms2_referrer_time\', null, 86400 * 365, true);\n\n        if (!$modx->user->isAuthenticated() && !empty($_REQUEST[$referrerVar])) {\n            $code = trim($_REQUEST[$referrerVar]);\n            if ($profile = $modx->getObject(\'msCustomerProfile\', array(\'referrer_code\' => $code))) {\n                $referrer = $profile->get(\'id\');\n                setcookie($cookieVar, $referrer, time() + $cookieTime);\n            }\n        }\n        break;\n\n    case \'OnUserSave\':\n        // Save referrer id\n        /** @var string $mode */\n        if ($mode == modSystemEvent::MODE_NEW) {\n            /** @var modUser $user */\n            $cookieVar = $modx->getOption(\'ms2_referrer_cookie_var\', null, \'msreferrer\', true);\n            $cookieTime = $modx->getOption(\'ms2_referrer_time\', null, 86400 * 365, true);\n            if ($modx->context->key != \'mgr\' && !empty($_COOKIE[$cookieVar])) {\n                if ($profile = $modx->getObject(\'msCustomerProfile\', array(\'id\' => $user->get(\'id\')))) {\n                    if (!$profile->get(\'referrer_id\') && $_COOKIE[$cookieVar] != $user->get(\'id\')) {\n                        $profile->set(\'referrer_id\', (int)$_COOKIE[$cookieVar]);\n                        $profile->save();\n                    }\n                }\n                setcookie($cookieVar, \'\', time() - $cookieTime);\n            }\n        }\n        break;\n\n    case \'msOnChangeOrderStatus\':\n        // Update customer stat\n        if (empty($status) || $status != 2) {\n            return;\n        }\n\n        /** @var modUser $user */\n        /** @var msOrder $order */\n        if ($user = $order->getOne(\'User\')) {\n            $q = $modx->newQuery(\'msOrder\', array(\'type\' => 0));\n            $q->innerJoin(\'modUser\', \'modUser\', array(\'modUser.id = msOrder.user_id\'));\n            $q->innerJoin(\'msOrderLog\', \'msOrderLog\', array(\n                \'msOrderLog.order_id = msOrder.id\',\n                \'msOrderLog.action\' => \'status\',\n                \'msOrderLog.entry\' => $status,\n            ));\n            $q->where(array(\'msOrder.user_id\' => $user->get(\'id\')));\n            $q->groupby(\'msOrder.user_id\');\n            $q->select(\'SUM(msOrder.cost)\');\n            if ($q->prepare() && $q->stmt->execute()) {\n                $spent = $q->stmt->fetchColumn();\n                /** @var msCustomerProfile $profile */\n                if ($profile = $modx->getObject(\'msCustomerProfile\', array(\'id\' => $user->get(\'id\')))) {\n                    $profile->set(\'spent\', $spent);\n                    $profile->save();\n                }\n            }\n        }\n        break;\n}', 0, NULL, 0, '', 0, 'core/components/minishop2/elements/plugins/plugin.minishop2.php'),
(17, 1, 0, 'modalConsole', '', 0, 19, 0, 'if ($modx->context->key != \'mgr\' || !$modx->getOption(\'modalconsole_enable\', null, true)) return;\n\nswitch ($modx->event->name) {\n    case \'OnManagerPageBeforeRender\':\n        $assetsUrl = $modx->getOption(\'modalconsole_assets_url\', null, $modx->getOption(\'assets_url\') . \'components/modalconsole/\');\n        if ($modx->hasPermission(\'console\')) {\n            $modx->controller->addLexiconTopic(\'modalconsole:default\');\n            $css = $modx->getOption(\'modalconsole_cssFile\', null, $assetsUrl.\'css/mgr/modalconsole.css\');\n            if ($css) $modx->controller->addCss($css);\n            $js = $modx->getOption(\'modalconsole_jsFile\', null, $assetsUrl.\'js/mgr/modalconsole.js\');\n            if ($js) $modx->controller->addJavascript($js);\n\n\n            $config = array(\n                \'connectorUrl\' => $assetsUrl.\'connector.php\',\n                \'limit\' => $modx->getOption(\'modalconsole_history_limit\', null, 20),\n            );\n            $_html = \"<script>modalConsole.config = {$modx->toJSON($config)} </script>\";\n            $modx->controller->addHtml($_html);\n        }\n        break;\n}', 0, NULL, 0, '', 0, 'core/components/modalconsole/elements/plugins/plugin.modalconsole.php');
