ey) ? $activeCls : \'\';\n        $placeholders = [\n            \'cultureKey\' => $cultureKey,\n            \'url\'        => $url,\n            \'active\'     => $active,\n            \'id\'         => $context->getOption(\'site_start\'),\n            \'language\'   => $languages[$cultureKey][\'Description\'],\n        ];\n\n        if (!empty($toArray)) {\n            $outputArray[] = $placeholders;\n        } else {\n            $chunk = $babel->getChunk($tpl, $placeholders);\n            if (!empty($chunk)) {\n                $outputArray[] = $chunk;\n            }\n        }\n    }\n}\n\nif (!empty($toArray)) {\n    return \'<pre>\'.print_r($outputArray, 1).\'</pre>\';\n}\n\n$output = implode($outputSeparator, $outputArray);\nif (!empty($wrapperTpl)) {\n    $output = $babel->getChunk($wrapperTpl, [\n        \'babelLinks\' => $output\n    ]);\n}\n\nif (!empty($toPlaceholder)) {\n    $modx->setPlaceholder($toPlaceholder, $output);\n    return;\n}\n\nreturn $output;', 0, 'a:6:{s:10:\"resourceId\";a:7:{s:4:\"name\";s:10:\"resourceId\";s:4:\"desc\";s:21:\"babellinks.resourceId\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";s:0:\"\";s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:16:\"babel:properties\";s:4:\"area\";s:0:\"\";}s:3:\"tpl\";a:7:{s:4:\"name\";s:3:\"tpl\";s:4:\"desc\";s:20:\"babeltranslation.tpl\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";s:0:\"\";s:5:\"value\";s:9:\"babelLink\";s:7:\"lexicon\";s:16:\"babel:properties\";s:4:\"area\";s:0:\"\";}s:9:\"activeCls\";a:7:{s:4:\"name\";s:9:\"activeCls\";s:4:\"desc\";s:26:\"babeltranslation.activeCls\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";s:0:\"\";s:5:\"value\";s:6:\"active\";s:7:\"lexicon\";s:16:\"babel:properties\";s:4:\"area\";s:0:\"\";}s:15:\"showUnpublished\";a:7:{s:4:\"name\";s:15:\"showUnpublished\";s:4:\"desc\";s:32:\"babeltranslation.showUnpublished\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";s:0:\"\";s:5:\"value\";b:0;s:7:\"lexicon\";s:16:\"babel:properties\";s:4:\"area\";s:0:\"\";}s