(47, 1, 0, 'rcv3', 'reCaptchaV3 hook for Formit', 0, 17, 0, '$recaptcha = $hook->getValue(\'g-recaptcha-response\');\n$url_google_api = \"https://www.google.com/recaptcha/api/siteverify\";\n$secret = $modx->getOption(\'formit.recaptcha_private_key\');\n$ip = $_SERVER[\'REMOTE_ADDR\'];\n\n$modx->lexicon->load(\'recaptchav3:default\');\n\nclass Captcha{\n    public function getCaptcha($SecretKey, $secret, $url_google_api, $ip){\n        $response = file_get_contents($url_google_api . \"?secret=\" . $secret . \"&response=\" . $SecretKey . \"&remoteip=\" . $ip);\n        $data = json_decode($response);\n        return $data;\n    }\n}\n\nif ($recaptcha) {\n    $ObjCaptcha = new Captcha();\n    $data = $ObjCaptcha->getCaptcha($recaptcha, $secret, $url_google_api, $ip); \n\n    if ($data->success) {\n        return true;\n    } else {   \n        $hook->addError(\'g-recaptcha-response\', $modx->lexicon(\'recaptchav3_check_error\'));\n        $error_message = \"\";\n        $error_message .= $modx->lexicon(\'recaptchav3_check_error_log\') . \"<br/>\";\n        foreach ($data->{\'error-codes\'} as $k => $v) {\n            $error_message .= \"{$k} - {$v}<br/>\";\n        }\n        $modx->log(MODX_LOG_LEVEL_ERROR, $error_message);\n        return false;\n    }\n} else {\n    $hook->addError(\'g-recaptcha-response\', $modx->lexicon(\'recaptchav3_check_empty_error\'));\n    $modx->log(MODX_LOG_LEVEL_ERROR, $modx->lexicon(\'recaptchav3_check_empty_error_log\'));\n    return false;\n}\n\nreturn true;', 0, 'a:0:{}', '', 0, 'core/components/recaptchav3/elements/snippets/recaptchav3.php'),
(48, 1, 0, 'rcv3_html', 'reCaptchaV3 snippet', 0, 17, 0, '$public = $modx->getOption(\'formit.recaptcha_public_key\');\n$action = $action ?: \'ajaxform\';\n\nif (!$modx->getPlaceholder(\'rcv3_initialized\')) {\n    $modx->regClientStartupScript(\'<script src=\"https://www.google.com/recaptcha/api.js?onload=ReCaptchaCallbackV3&render=\' . $public . \'\" async></script>\');\n    $modx->regClientScript(\'\n        <script>\n            var ReCaptchaCallbackV3 = function() {\n                grecaptcha.ready(function() {\n                    grecaptcha.reset = grecaptchaExecute;\n                    grecaptcha.reset();\n                });\n            };\n            function grecaptchaExecute() {\n                grecaptcha.execute(\"\' . $public . \'\", { action: \"\' . $action . \'\" }).then(function(token) {\n                    var fieldsToken = document.querySelectorAll(\"[name =\\\'g-recaptcha-response\\\']\");\n                    Array.prototype.forEach.call(fieldsToken, function(el, i){\n                        el.value = token;\n                    });\n                });\n            };\n            // обновляем капчу каждую минуту\n            setInterval(function() {\n                grecaptcha.reset();\n            }, 60000);\n        </script>\n    \', true);\n    $modx->setPlaceholder(\'rcv3_initialized\', 1);\n}\n\n$output = \'\n    <span class=\"error_g-recaptcha-response error\">\' . $error . \'</span>\n    <input type=\"hidden\" name=\"g-recaptcha-response\">\n\';\n\nreturn $output;', 0, 'a:0:{}', '', 0, 'core/components/recaptchav3/elements/snippets/recaptchav3_html.php'),
(49, 1, 0, 'bb', '', 0, 0, 0, '$babel = $modx->getService(\'babel\', \'Babel\', $modx->getOption(\'babel.core_path\', null, $modx->getOption(\'core_path\').\'components/babel/\').\'model/babel/\', $scriptProperties);\n\n/* be sure babel and babel TV is loaded */\nif (!($babel instanceof Babel) || !$babel->babelTv)\n    return intval($input);\n\n/* get snippet properties */\n$resourceId = intval($input);\n\nif (empty($resourceId)) {\n    return;\n}\n\nif (empty($options[\'contextKey\'])) { \n    $contextKey = $modx->context->key;\n}else{\n    $contextKey = $options[\'contextKey\'];\n}\n \n/* determine id of tranlated resource */\n$linkedResources = $babel->getLinkedResources($resourceId);\n$output          = null;\nif (isset($linkedResources[$contextKey])) {\n    $resource = $modx->getObject(\'modResource\', $linkedResources[$contextKey]);\n    if ($resource) {\n        $output = $resource->get(\'id\');\n    }\n}\n\nif(empty($output)){\n    return intval($input);\n}else{\n    return $output;\n}', 0, 'a:0:{}', '', 0, '');
