(54, 1, 0, 'msOptions', '', 0, 18, 0, '/** @var modX $modx */\n/** @var array $scriptProperties */\n/** @var miniShop2 $miniShop2 */\n$miniShop2 = $modx->getService(\'miniShop2\');\n\n$tpl = $modx->getOption(\'tpl\', $scriptProperties, \'tpl.msOptions\');\nif (!empty($input) && empty($product)) {\n    $product = $input;\n}\nif (!empty($name) && empty($options)) {\n    $options = $name;\n}\n\n$product = !empty($product) && $product != $modx->resource->id\n    ? $modx->getObject(\'msProduct\', array(\'id\' => $product))\n    : $modx->resource;\nif (!($product instanceof msProduct)) {\n    return \"[msOptions] The resource with id = {$product->id} is not instance of msProduct.\";\n}\n\n$names = array_map(\'trim\', explode(\',\', $options));\n$options = array();\nforeach ($names as $name) {\n    if (!empty($name) && $option = $product->get($name)) {\n        if (!is_array($option)) {\n            $option = array($option);\n        }\n        if (isset($option[0]) and (trim($option[0]) != \'\')) {\n            $options[$name] = $option;\n        }\n    }\n}\n\n$options = $miniShop2->sortOptionValues($options, $scriptProperties[\'sortOptionValues\']);\n\n/** @var pdoTools $pdoTools */\n$pdoTools = $modx->getService(\'pdoTools\');\n\nreturn $pdoTools->getChunk($tpl, array(\n    \'id\' => $product->id,\n    \'options\' => $options,\n));', 0, 'a:4:{s:7:\"product\";a:7:{s:4:\"name\";s:7:\"product\";s:4:\"desc\";s:16:\"ms2_prop_product\";s:4:\"type\";s:11:\"numberfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:7:\"options\";a:7:{s:4:\"name\";s:7:\"options\";s:4:\"desc\";s:16:\"ms2_prop_options\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:16:\"sortOptionValues\";a:7:{s:4:\"name\";s:16:\"sortOptionValues\";s:4:\"desc\";s:25:\"ms2_prop_sortOptionValues\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a: