(56, 1, 0, 'msGetOrder', '', 0, 18, 0, '/** @var modX $modx */\n/** @var array $scriptProperties */\n/** @var miniShop2 $miniShop2 */\n$miniShop2 = $modx->getService(\'miniShop2\');\n$miniShop2->initialize($modx->context->key);\n/** @var pdoFetch $pdoFetch */\n$fqn = $modx->getOption(\'pdoFetch.class\', null, \'pdotools.pdofetch\', true);\n$path = $modx->getOption(\'pdofetch_class_path\', null, MODX_CORE_PATH . \'components/pdotools/model/\', true);\nif ($pdoClass = $modx->loadClass($fqn, $path, false, true)) {\n    $pdoFetch = new $pdoClass($modx, $scriptProperties);\n} else {\n    return false;\n}\n$pdoFetch->addTime(\'pdoTools loaded.\');\n\n$tpl = $modx->getOption(\'tpl\', $scriptProperties, \'tpl.msGetOrder\');\n\nif (empty($id) && !empty($_GET[\'msorder\'])) {\n    $id = (int)$_GET[\'msorder\'];\n}\nif (empty($id)) {\n    return;\n}\n/** @var msOrder $order */\nif (!$order = $modx->getObject(\'msOrder\', compact(\'id\'))) {\n    return $modx->lexicon(\'ms2_err_order_nf\');\n}\n$canView = (!empty($_SESSION[\'minishop2\'][\'orders\']) && in_array($id, $_SESSION[\'minishop2\'][\'orders\'])) ||\n    $order->get(\'user_id\') == $modx->user->id || $modx->user->hasSessionContext(\'mgr\') || !empty($scriptProperties[\'id\']);\nif (!$canView) {\n    return \'\';\n}\n\n// Select ordered products\n$where = array(\n    \'msOrderProduct.order_id\' => $id,\n);\n\n// Include products properties\n$leftJoin = array(\n    \'msProduct\' => array(\n        \'class\' => \'msProduct\',\n        \'on\' => \'msProduct.id = msOrderProduct.product_id\',\n    ),\n    \'Data\' => array(\n        \'class\' => \'msProductData\',\n        \'on\' => \'msProduct.id = Data.id\',\n    ),\n    \'Vendor\' => array(\n        \'class\' => \'msVendor\',\n        \'on\' => \'Data.vendor = Vendor.id\',\n    ),\n);\n\n// Select columns\n$select = array(\n    \'msProduct\' => !empty($includeContent)\n        ? $modx->getSelectColumns(\'msProduct\', \'msProduct\')\n        : $modx->getSelectColumns(\'msProduct\', \'msProduct\', \'\', array(\'content\'), true),\n    \'Data\' => $modx->getSelectColumns(\n        \'msProductData\',\n        \'Data\',\n        \'\',\n        array(\'id\'),\n        true\n    ) . \',`Data`.`price` as `original_price`\',\n    \'Vendor\' => $modx->getSelectColumns(\'msVendor\', \'Vendor\', \'vendor.\', array(\'id\'), true),\n    \'OrderProduct\' => $modx->getSelectColumns(\'msOrderProduct\', \'msOrderProduct\', \'\', array(\'id\'), true) . \', `msOrderProduct`.`id` as `order_product_id`\',\n);\n\n// Include products thumbnails\nif (!empty($includeThumbs)) {\n    $thumbs = array_map(\'trim\', explode(\',\', $includeThumbs));\n    if (!empty($thumbs[0])) {\n        foreach ($thumbs as $thumb) {\n            $leftJoin[$thumb] = array(\n                \'class\' => \'msProductFile\',\n                \'on\' => \"`{$thumb}`.product_id = msProduct.id AND `{$thumb}`.parent != 0 AND `{$thumb}`.path LIKE \'%/{$thumb}/%\'\",\n            );\n            $select[$thumb] = \"`{$thumb}`.url as \'{$thumb}\'\";\n        }\n        $pdoFetch->addTime(\'Included list of thumbnails: <b>\' . implode(\', \', $thumbs) . \'</b>.\');\n    }\n}\n\n// Add user parameters\nforeach (array(\'where\', \'leftJoin\', \'select\') as $v) {\n    if (!empty($scriptProperties[$v])) {\n        $tmp = $scriptProperties[$v];\n        if (!is_array($tmp)) {\n            $tmp = json_decode($tmp, true);\n        }\n        if (is_array($tmp)) {\n            $$v = array_merge($$v, $tmp);\n        }\n    }\n    unset($scriptProperties[$v]);\n}\n$pdoFetch->addTime(\'Conditions prepared\');\n\n// Tables for joining\n$default = array(\n    \'class\' => \'msOrderProduct\',\n    \'where\' => $where,\n    \'leftJoin\' => $leftJoin,\n    \'select\' => $select,\n    \'joinTVsTo\' => \'msProduct\',\n    \'sortby\' => \'msOrderProduct.id\',\n    \'sortdir\' => \'asc\',\n    \'groupby\' => \'msOrderProduct.id\',\n    \'fastMode\' => false,\n    \'limit\' => 0,\n    \'return\' => \'data\',\n    \'decodeJSON\' => true,\n    \'nestedChunkPrefix\' => \'minishop2_\',\n);\n// Merge all properties and run!\n$pdoFetch->setConfig(array_merge($default, $scriptProperties), true);\n$rows = $pdoFetch->run();\n\n$products = array();\n$cart_count = 0;\nforeach ($rows as $product) {\n    $product[\'old_price\'] = $miniShop2->formatPrice(\n        $product[\'original_price\'] > $product[\'price\']\n            ? $product[\'original_price\']\n            : $product[\'old_price\']\n    );\n    $product[\'price\'] = $miniShop2->formatPrice($product[\'price\']);\n    $product[\'cost\'] = $miniShop2->formatPrice($product[\'cost\']);\n    $product[\'weight\'] = $miniShop2->formatWeight($product[\'weight\']);\n\n    $product[\'id\'] = (int)$product[\'id\'];\n    if (empty($product[\'name\'])) {\n        $product[\'name\'] = $product[\'pagetitle\'];\n    } else {\n        $product[\'pagetitle\'] = $product[\'name\'];\n    }\n\n    // Additional properties of product\n    if (!empty($product[\'options\']) && is_array($product[\'options\'])) {\n        foreach ($product[\'options\'] as $option => $value) {\n            $product[\'option.\' . $option] = $value;\n        }\n    }\n\n    // Add option values\n    $options = $modx->call(\'msProductData\', \'loadOptions\', array($modx, $product[\'id\']));\n    $products[] = array_merge($product, $options);\n\n    // Count total\n    $cart_count += $product[\'count\'];\n}\n\n$pls = array_merge($scriptProperties, array(\n    \'order\' => $order->toArray(),\n    \'products\' => $products,\n    \'user\' => ($tmp = $order->getOne(\'User\'))\n        ? array_merge($tmp->getOne(\'Profile\')->toArray(), $tmp->toArray())\n        : array(),\n    \'address\' => ($tmp = $order->getOne(\'Address\'))\n        ? $tmp->toArray()\n        : array(),\n    \'delivery\' => ($tmp = $order->getOne(\'Delivery\'))\n        ? $tmp->toArray()\n        : array(),\n    \'payment\' => ($payment = $order->getOne(\'Payment\'))\n        ? $payment->toArray()\n        : array(),\n    \'total\' => array(\n        \'cost\' => $miniShop2->formatPrice($order->get(\'cost\')),\n        \'cart_cost\' => $miniShop2->formatPrice($order->get(\'cart_cost\')),\n        \'delivery_cost\' => $miniShop2->formatPrice($order->get(\'delivery_cost\')),\n        \'weight\' => $miniShop2->formatWeight($order->get(\'weight\')),\n        \'cart_weight\' => $miniShop2->formatWeight($order->get(\'weight\')),\n        \'cart_count\' => $cart_count,\n    ),\n));\n\n// add \"payment\" link\nif ($payment and $class = $payment->get(\'class\')) {\n    $status = $modx->getOption(\'payStatus\', $scriptProperties, \'1\');\n    $status = array_map(\'trim\', explode(\',\', $status));\n    if (in_array($order->get(\'status\'), $status)) {\n        $miniShop2->loadCustomClasses(\'payment\');\n        if (class_exists($class)) {\n            /** @var msPaymentHandler|PayPal $handler */\n            $handler = new $class($order);\n            if (method_exists($handler, \'getPaymentLink\')) {\n                $link = $handler->getPaymentLink($order);\n                $pls[\'payment_link\'] = $link;\n            }\n        }\n    }\n}\n\n$output = $pdoFetch->getChunk($tpl, $pls);\n\nif ($modx->user->hasSessionContext(\'mgr\') && !empty($showLog)) {\n    $output .= \'<pre class=\"msGetOrderLog\">\' . print_r($pdoFetch->getTime(), true) . \'</pre>\';\n}\n\nif (!empty($toPlaceholder)) {\n    $modx->setPlaceholder($toPlaceholder, $output);\n} else {\n    return $output;\n}', 0, 'a:5:{s:3:\"tpl\";a:7:{s:4:\"name\";s:3:\"tpl\";s:4:\"desc\";s:12:\"ms2_prop_tpl\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:14:\"tpl.msGetOrder\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:10:\"includeTVs\";a:7:{s:4:\"name\";s:10:\"includeTVs\";s:4:\"desc\";s:19:\"ms2_prop_includeTVs\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:13:\"includeThumbs\";a:7:{s:4:\"name\";s:13:\"includeThumbs\";s:4:\"desc\";s:22:\"ms2_prop_includeThumbs\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:13:\"toPlaceholder\";a:7:{s:4:\"name\";s:13:\"toPlaceholder\";s:4:\"desc\";s:22:\"ms2_prop_toPlaceholder\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:7:\"showLog\";a:7:{s:4:\"name\";s:7:\"showLog\";s:4:\"desc\";s:16:\"ms2_prop_showLog\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:0;s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}}', '', 0, 'core/components/minishop2/elements/snippets/snippet.ms_get_order.php'),
(57, 1, 0, 'msProductOptions', '', 0, 18, 0, '/** @var modX $modx */\n/** @var array $scriptProperties */\n/** @var miniShop2 $miniShop2 */\n$miniShop2 = $modx->getService(\'miniShop2\');\n\n$tpl = $modx->getOption(\'tpl\', $scriptProperties, \'tpl.msProductOptions\');\nif (!empty($input) && empty($product)) {\n    $product = $input;\n}\n\n$product = !empty($product) && $product != $modx->resource->id\n    ? $modx->getObject(\'msProduct\', array(\'id\' => $product))\n    : $modx->resource;\nif (!($product instanceof msProduct)) {\n    return \"[msProductOptions] The resource with id = {$product->id} is not instance of msProduct.\";\n}\n\n$ignoreGroups = array_diff(array_map(\'trim\', explode(\',\', $modx->getOption(\'ignoreGroups\', $scriptProperties, \'\'))), array(\'\'));\n$ignoreOptions = array_diff(array_map(\'trim\', explode(\',\', $modx->getOption(\'ignoreOptions\', $scriptProperties, \'\'))), array(\'\'));\n$sortGroups = array_diff(array_map(\'trim\', explode(\',\', $modx->getOption(\'sortGroups\', $scriptProperties, \'\'))), array(\'\'));\n$sortOptions = array_diff(array_map(\'trim\', explode(\',\', $modx->getOption(\'sortOptions\', $scriptProperties, \'\'))), array(\'\'));\n$onlyOptions = array_diff(array_map(\'trim\', explode(\',\', $modx->getOption(\'onlyOptions\', $scriptProperties, \'\'))), array(\'\'));\nif (empty($sortOptions) && !empty($onlyOptions)) {\n    $sortOptions = $onlyOptions;\n}\n$groups = !empty($groups)\n    ? array_map(\'trim\', explode(\',\', $groups))\n    : array();\n/** @var msProductData $data */\nif ($data = $product->getOne(\'Data\')) {\n    $optionKeys = $data->getOptionKeys();\n}\nif (empty($optionKeys)) {\n    return \'\';\n}\n$productData = $product->loadOptions();\n\n$options = array();\nforeach ($optionKeys as $key) {\n    // Filter by key\n    if (!empty($onlyOptions) && $onlyOptions[0] != \'\' && !in_array($key, $onlyOptions)) {\n        continue;\n    } elseif (in_array($key, $ignoreOptions)) {\n        continue;\n    }\n    $option = array();\n    foreach ($productData as $dataKey => $dataValue) {\n        $dataKey = explode(\'.\', $dataKey);\n        if ($dataKey[0] == $key && (count($dataKey) > 1)) {\n            $option[$dataKey[1]] = $dataValue;\n        }\n    }\n\n    $skip = (!empty($ignoreGroups) && (in_array($option[\'category\'], $ignoreGroups) || in_array($option[\'category_name\'], $ignoreGroups)))\n        || (!empty($groups) && !in_array($option[\'category\'], $groups) && !in_array($option[\'category_name\'], $groups));\n\n    if (!$skip) {\n        $option[\'value\'] = $product->get($key);\n        if (!empty($option[\'value\'])) {\n            $options[$key] = $option;\n        }\n    }\n}\n\nif (!empty($sortGroups) && !empty($options)) {\n    $sortGroups = array_map(\'mb_strtolower\', $sortGroups);\n    uasort($options, function ($a, $b) use ($sortGroups) {\n        $ai = array_search(mb_strtolower($a[\'category\'], \'utf-8\'), $sortGroups, true);\n        $ai = $ai !== false ? $ai : array_search(mb_strtolower($a[\'category_name\'], \'utf-8\'), $sortGroups, true);\n        $bi = array_search(mb_strtolower($b[\'category\'], \'utf-8\'), $sortGroups, true);\n        $bi = $bi !== false ? $bi : array_search(mb_strtolower($b[\'category_name\'], \'utf-8\'), $sortGroups, true);\n        if ($ai === false && $bi === false) {\n            return 0;\n        } elseif ($ai === false) {\n            return 1;\n        } elseif ($bi === false) {\n            return -1;\n        } elseif ($ai < $bi) {\n            return -1;\n        } elseif ($ai > $bi) {\n            return 1;\n        }\n        return 0;\n    });\n}\n\nif (!empty($sortOptions) && !empty($options)) {\n    $sortOptions = array_map(\'mb_strtolower\', $sortOptions);\n    uksort($options, function ($a, $b) use ($sortOptions) {\n        $ai = array_search(mb_strtolower($a, \'utf-8\'), $sortOptions, true);\n        $bi = array_search(mb_strtolower($b, \'utf-8\'), $sortOptions, true);\n        if ($ai === false && $bi === false) {\n            return 0;\n        } elseif ($ai === false) {\n            return 1;\n        } elseif ($bi === false) {\n            return -1;\n        } elseif ($ai < $bi) {\n            return -1;\n        } elseif ($ai > $bi) {\n            return 1;\n        }\n        return 0;\n    });\n}\n\n$options = $miniShop2->sortOptionValues($options, $scriptProperties[\'sortOptionValues\']);\n\nif (in_array($scriptProperties[\'return\'], array(\'data\', \'array\'), true)) {\n    return $options;\n}\n\n/** @var pdoTools $pdoTools */\n$pdoTools = $modx->getService(\'pdoTools\');\n\nreturn $pdoTools->getChunk($tpl, array(\n    \'options\' => $options,\n));', 0, 'a:9:{s:7:\"product\";a:7:{s:4:\"name\";s:7:\"product\";s:4:\"desc\";s:16:\"ms2_prop_product\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:3:\"tpl\";a:7:{s:4:\"name\";s:3:\"tpl\";s:4:\"desc\";s:12:\"ms2_prop_tpl\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:20:\"tpl.msProductOptions\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:12:\"ignoreGroups\";a:7:{s:4:\"name\";s:12:\"ignoreGroups\";s:4:\"desc\";s:21:\"ms2_prop_ignoreGroups\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:13:\"ignoreOptions\";a:7:{s:4:\"name\";s:13:\"ignoreOptions\";s:4:\"desc\";s:22:\"ms2_prop_ignoreOptions\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:11:\"onlyOptions\";a:7:{s:4:\"name\";s:11:\"onlyOptions\";s:4:\"desc\";s:20:\"ms2_prop_onlyOptions\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";s:20:\"minishop2:properties\";s:4:\"area\";s:0:\"\";}s:10:\"sortGroups\";a:7:{s:4:\"name\";s:10:\"sortGroups\";s:4:\"desc\";s:19:\"ms2_prop