(58, 1, 0, 'wmdSocials', 'wmdSocials snippet to list items', 0, 23, 0, '/** @var modX $modx */\n/** @var array $scriptProperties */\n/** @var wmdSocials $wmdSocials */\n$wmdSocials = $modx->getService(\'wmdSocials\', \'wmdSocials\', MODX_CORE_PATH . \'components/wmdsocials/model/\', $scriptProperties);\nif (!$wmdSocials) {\n    return \'Could not load wmdSocials class!\';\n}\n\n// Do your snippet code here. This demo grabs 5 items from our custom table.\n$tpl = $modx->getOption(\'tpl\', $scriptProperties, \'Item\');\n$sortby = $modx->getOption(\'sortby\', $scriptProperties, \'name\');\n$sortdir = $modx->getOption(\'sortbir\', $scriptProperties, \'ASC\');\n$limit = $modx->getOption(\'limit\', $scriptProperties, 5);\n$outputSeparator = $modx->getOption(\'outputSeparator\', $scriptProperties, \"\\n\");\n$toPlaceholder = $modx->getOption(\'toPlaceholder\', $scriptProperties, false);\n\n// Build query\n$c = $modx->newQuery(\'wmdSocialsItem\');\n$c->sortby($sortby, $sortdir);\n$c->where([\'active\' => 1]);\n$c->limit($limit);\n$items = $modx->getIterator(\'wmdSocialsItem\', $c);\n\n// Iterate through items\n$list = [];\n/** @var wmdSocialsItem $item */\nforeach ($items as $item) {\n    $list[] = $modx->getChunk($tpl, $item->toArray());\n}\n\n// Output\n$output = implode($outputSeparator, $list);\nif (!empty($toPlaceholder)) {\n    // If using a placeholder, output nothing and set output to specified placeholder\n    $modx->setPlaceholder($toPlaceholder, $output);\n\n    return \'\';\n}\n// By default just return output\nreturn $output;', 0, 'a:6:{s:3:\"tpl\";a:7:{s:4:\"name\";s:3:\"tpl\";s:4:\"desc\";s:19:\"wmdsocials_prop_tpl\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:19:\"tpl.wmdSocials.item\";s:7:\"lexicon\";s:21:\"wmdsocials:properties\";s:4:\"area\";s:0:\"\";}s:6:\"sortby\";a:7:{s:4:\"name\";s:6:\"sortby\";s:4:\"desc\";s:22:\"wmdsocials_prop_sortby\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:4:\"name\";s:7:\"lexicon\";s:21:\"wmdsocials:properties\";s:4:\"area\";s:0:\"\";}s:7:\"sortdir\";a:7:{s:4:\"name\";s:7:\"sortdir\";s:4:\"desc\";s:23:\"wmdsocials_prop_sortdir\";s:4:\"type\";s:4:\"list\";s:7:\"options\";a:2:{i:0;a:2:{s:4:\"text\";s:3:\"ASC\";s:5:\"value\";s:3:\"ASC\";}i:1;a:2:{s:4:\"text\";s:4:\"DESC\";s:5:\"value\";s:4:\"DESC\";}}s:5:\"value\";s:3:\"ASC\";s:7:\"lexicon\";s:21:\"wmdsocials:properties\";s:4:\"area\";s:0:\"\";}s:5:\"limit\";a:7:{s:4:\"name\";s:5:\"limit\";s:4:\"desc\";s:21:\"wmdsocials_prop_limit\";s:4:\"type\";s:11:\"numberfield\";s:7:\"options\";a:0:{}s:5:\"value\";i:10;s:7:\"lexicon\";s:21:\"wmdsocials:properties\";s:4:\"area\";s:0:\"\";}s:15:\"outputSeparator\";a:7:{s:4:\"name\";s:15:\"outputSeparator\";s:4:\"desc\";s:31:\"wmdsocials_prop_outputSeparator\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:1:\"\n\";s:7:\"lexicon\";s:21:\"wmdsocials:properties\";s:4:\"area\";s:0:\"\";}s:13:\"toPlaceholder\";a:7:{s:4:\"name\";s:13:\"toPlaceholder\";s:4:\"desc\";s:29:\"wmdsocials_prop_toPlaceholder\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:0;s:7:\"lexicon\";s:21:\"wmdsocials:properties\";s:4:\"area\";s:0:\"\";}}', '', 0, 'core/components/wmdsocials/elements/snippets/wmdsocials.php');
